# Variables hold data
age = 23          # int (integer)
height = 5.6      # float (decimal)
name = "Alice"   # str (string)
is_student = True  # bool (boolean)

# Printing out the values
print("Name:", name) 
print("Age:", age)            
print("Height:", height)      
print("Is a student?", is_student)  

#check data type
print(type(age)) #output: integer
print(type(height)) #output:float
print(type(name)) #output:string
print(type(is_student)) #output:boolean

#dynamic typing
age = 23
print(age)
age = "now I'm 20"
